package com.dbs.training.demoproject;

public class Calculator {
	
	public int
	add(int a,int b) {
		return a+b;
	}
	public int sub(int a,int b) {
		
			return a-b;
		}
	

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Calculator c = new Calculator();
		c.add(5, 5);
		c.sub(10, 5);
	}
	}
	

